import styled from 'styled-components';

export const DropDown = styled.div`
position:absolute;
top:0;
left:0;
width:80%;
height: 100vh;
background: #000;
`;

export const DropUp =styled.div`
display:flex;
justify-content: space-between;
align-items:center;
`;

export const NavLogo= styled.div`
display:flex;
align-items:center;
`;

export const BeRide= styled.h1`
font-size: 20px;
color:#fff;
`;
