import styled from 'styled-components';

export const TextD =styled.div`
position:fixed;
top: 50%;
left: 4%;
transform: translate(-50% , -4%);
height: 400px;
width:400px;

@media screen and (max-)
`;

export const Text =styled.h1`
font-size: 24px;
color: #000;
font-weight: 900;
transform: rotate(-90deg);
`;
