    const [ {basket }, dispatch] = useStateValue();

    return (
        <>
            <Checkouth1>Your Basket</Checkouth1>
            <CheckoutList>
                {basket.map(item => (
                    <CheckoutProduct
                    />
                ) )}
                
            </CheckoutList>
        </>
    )
}

export default Checkout
